mapper.select:
 select Count(1) from `app_info` 


and `createdBy`=#{devId}


and `softwareName` like CONCAT('%',#{softwareName},'%')


and `status`=#{status}


and `categoryLevel1`=#{categoryLevel1}


and `categoryLevel2`=#{categoryLevel2}


and `categoryLevel3`=#{categoryLevel3}


and `flatformId`=#{flatformId}

SELECT a.`id`,a.`softwareName`,a.`APKName`,a.`softwareSize`,a.`supportROM`,a.`downloads`, a.`devId`, (SELECT `devName` FROM `dev_user` WHERE `id`=a.`devId`) AS devName, a.`status`, (SELECT d.`valueName` FROM `data_dictionary` d WHERE d.`valueId`=a.`status` AND d.`typeCode`="APP_STATUS") AS statusName, a.`flatformId`, (SELECT d.`valueName` FROM `data_dictionary` d WHERE d.`valueId`=a.`flatformId` AND d.`typeCode`="APP_FLATFORM") AS flatformName, a.`categoryLevel1`, (SELECT c.`categoryName` FROM `app_category` c WHERE c.`id`=a.`categoryLevel1`) AS categoryLevel1Name, a.`categoryLevel2`, (SELECT c.`categoryName` FROM `app_category` c WHERE c.`id`=a.`categoryLevel2`) AS categoryLevel2Name, a.`categoryLevel3`, (SELECT c.`categoryName` FROM `app_category` c WHERE c.`id`=a.`categoryLevel3`) AS categoryLevel3Name, a.`versionId`, (SELECT v.`versionNo` FROM `app_version` v WHERE v.`id`=a.`versionId`) AS versionNo FROM `app_info` a
and `devId`=#{devId}
and `softwareName`like CONCAT('%',#{softwareName},'%')
and `status`=#{status}
and `categoryLevel1`=#{categoryLevel1}
and `categoryLevel2`=#{categoryLevel2}
and `categoryLevel3`=#{categoryLevel3}
and `flatformId`=#{flatformId}

 SELECT Count(1) FROM `app_info`



and `softwareName` like CONCAT('%',#{softwareName},'%')


and `status`=#{status}


and `categoryLevel1`=#{categoryLevel1}


and `categoryLevel2`=#{categoryLevel2}


and `categoryLevel3`=#{categoryLevel3}


and `flatformId`=#{flatformId}

SELECT a.`id`,a.`softwareName`,a.`APKName`,a.`softwareSize`,a.`supportROM`,a.`downloads`, a.`status`, (SELECT d.`valueName` FROM `data_dictionary` d WHERE d.`valueId`=a.`status` AND d.`typeCode`="APP_STATUS") AS statusName, a.`flatformId`, (SELECT d.`valueName` FROM `data_dictionary` d WHERE d.`valueId`=a.`flatformId` AND d.`typeCode`="APP_FLATFORM") AS flatformName, a.`categoryLevel1`, (SELECT c.`categoryName` FROM `app_category` c WHERE c.`id`=a.`categoryLevel1`) AS categoryLevel1Name, a.`categoryLevel2`, (SELECT c.`categoryName` FROM `app_category` c WHERE c.`id`=a.`categoryLevel2`) AS categoryLevel2Name, a.`categoryLevel3`, (SELECT c.`categoryName` FROM `app_category` c WHERE c.`id`=a.`categoryLevel3`) AS categoryLevel3Name, a.`versionId`, (SELECT v.`versionNo` FROM `app_version` v WHERE v.`id`=a.`versionId`) AS versionNo FROM `app_info` a
and `softwareName`like CONCAT('%',#{softwareName},'%')
and `status`=#{status}
and `categoryLevel1`=#{categoryLevel1}
and `categoryLevel2`=#{categoryLevel2}
and `categoryLevel3`=#{categoryLevel3}
and `flatformId`=#{flatformId}
